@Injectable({
  providedIn: 'root'
})
export class BeautifulSelectService {

  constructor() { }
}
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'ngx-beautiful-select',
  template: `
    <p>
      beautiful-select works!
    </p>
  `,
  styles: [
  ]
})
export class BeautifulSelectComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}
